#设置浏览器发送服务器的文件脚本为UTF8
SET NAMES UTF8;
#丢弃数据库如果存在
DROP DATABASE  IF EXISTS huawei;
#创建数据库设置服务器保存到数据库的的数据脚本为UTF8
CREATE DATABASE huawei CHARSET=UTF8;
#进入数据库
USE huawei;
#创建数据表
CREATE TABLE hw_user(
  uid INT PRIMARY KEY AUTO_INCREMENT,
  uname VARCHAR(8),
  upwd  INT,
  email VARCHAR(22),
  gender BOOL
);

#轮播表
CREATE TABLE hw_index_carousel(
   cid INT PRIMARY KEY AUTO_INCREMENT,
   img CHAR(128),
   title  VARCHAR(400),
   href  CHAR(128)
);


#hw_index_product
CREATE TABLE hw_index_product(
 lid INT PRIMARY KEY AUTO_INCREMENT,
 title VARCHAR(200),
 welfare CHAR(50),
 pic VARCHAR(100),
 price DECIMAL(8,2),
 href VARCHAR(100),
 fiery CHAR(50)  DEFAULT ''
);
 
  CREATE TABLE hw_product_details(
   lid INT ,
   pid INT,
   title VARCHAR(300),
   subtitle VARCHAR(300),
   welfare VARCHAR(100),
   price DECIMAL(8,2),
   dat CHAR(128),
   encode INT,
   product_pname CHAR(128),
   details_img1 CHAR(66),
   details_img2 CHAR(66),
   details_img3 CHAR(66)
);



